helpers.code:
  vec2 rot(vec2 p, float a)
{
vec2 r;
float sa = sin(a), ca = cos(a);
r.x = p.x*ca - p.y*sa;
r.y = p.x*sa + p.y*ca;
return r;
}

const vec3 GDFVectors[19] = vec3[]( normalize(vec3(1, 0, 0)), normalize(vec3(0, 1, 0)), normalize(vec3(0, 0, 1)),
normalize(vec3(1, 1, 1 )), normalize(vec3(-1, 1, 1)), normalize(vec3(1, -1, 1)), normalize(vec3(1, 1, -1)),
normalize(vec3(0, 1, PHI+1)), normalize(vec3(0, -1, PHI+1)), normalize(vec3(PHI+1, 0, 1)), normalize(vec3(-PHI-1, 0, 1)), normalize(vec3(1, PHI+1, 0)), normalize(vec3(-1, PHI+1, 0)),
normalize(vec3(0, PHI, 1)), normalize(vec3(0, -PHI, 1)), normalize(vec3(1, 0, PHI)), normalize(vec3(-1, 0, PHI)), normalize(vec3(PHI, 1, 0)), normalize(vec3(-PHI, 1, 0)) );

node.function:
        float fBox(vec3 p, vec3 b) {
vec3 d = abs(p) - b;
return length(max(d, vec3(0))) + vmax(min(d, vec3(0)));
}

        float fCylinder(vec3 p, float r, float height) {
float d = length(p.xz) - r;
d = max(d, abs(p.y) - height);
return d;
}

        float fCapsule(vec3 p, float r, float c) {
return mix(length(p.xz) - r, length(vec3(p.x, abs(p.y) - c, p.z)) - r, step(c, abs(p.y)));
}

        float fCapsule(vec3 p, vec3 a, vec3 b, float r) {
return fLineSegment(p, a, b) - r;
}

        float fLineSegment(vec3 p, vec3 a, vec3 b) {
vec3 ab = b - a;
float t = saturate(dot(p - a, ab) / dot(ab, ab));
return length((ab*t + a) - p);
}

        float fTorus(vec3 p, float smallRadius, float largeRadius) {
return length(vec2(length(p.xz) - largeRadius, p.y)) - smallRadius;
}

        float fCircle(vec3 p, float r) {
float l = length(p.xz) - r;
return length(vec2(p.y, l));
}

        float fDisc(vec3 p, float r) {
float l = length(p.xz) - r;
return l < 0 ? abs(p.y) : length(vec2(p.y, l));
}

        float fHexagonCircumcircle(vec3 p, vec2 h) {
vec3 q = abs(p);
return max(q.y - h.y, max(q.x*sqrt(3)*0.5 + q.z*0.5, q.z) - h.x);
}

        float fHexagonIncircle(vec3 p, vec2 h) {
return fHexagonCircumcircle(p, vec2(h.x*sqrt(3)*0.5, h.y));
}

        float fCone(vec3 p, float radius, float height) {
vec2 q = vec2(length(p.xz), p.y);
vec2 tip = q - vec2(0, height);
vec2 mantleDir = normalize(vec2(height, radius));
float mantle = dot(tip, mantleDir);
float d = max(mantle, -q.y);
float projected = dot(tip, vec2(mantleDir.y, -mantleDir.x));

if ((q.y > height) && (projected < 0)) {
d = max(d, length(tip));
}

if ((q.x > radius) && (projected > length(vec2(height, radius)))) {
d = max(d, length(q - vec2(radius, 0)));
}
return d;
}

        float fGDF(vec3 p, float r, int begin, int end) {
float d = 0;
for (int i = begin; i <= end; ++i)
d = max(d, abs(dot(p, GDFVectors[i])));
return d - r;
}

        float fOpUnionChamfer(float a, float b, float r) {
return min(min(a, b), (a - r + b)*sqrt(0.5));
}

        float fOpIntersectionChamfer(float a, float b, float r) {
return max(max(a, b), (a + r + b)*sqrt(0.5));
}

        float fOpDifferenceChamfer (float a, float b, float r) {
return fOpIntersectionChamfer(a, -b, r);
}

        float fOpUnionRound(float a, float b, float r) {
vec2 u = max(vec2(r - a,r - b), vec2(0));
return max(r, min (a, b)) - length(u);
}

        float fOpIntersectionRound(float a, float b, float r) {
vec2 u = max(vec2(r + a,r + b), vec2(0));
return min(-r, max (a, b)) + length(u);
}

        float fOpDifferenceRound (float a, float b, float r) {
return fOpIntersectionRound(a, -b, r);
}

        float fOpUnionColumns(float a, float b, float r, float n) {
if ((a < r) && (b < r)) {
vec2 p = vec2(a, b);
float columnradius = r*sqrt(2)/((n-1)*2+sqrt(2));
rot45(p);
p.x -= sqrt(2)/2*r;
p.x += columnradius*sqrt(2);
if (mod(n,2) == 1) {
p.y += columnradius;
}
rep1(p.y, columnradius*2);
float result = length(p) - columnradius;
result = min(result, p.x);
result = min(result, a);
return min(result, b);
} else {
return min(a, b);
}
}

        float fOpDifferenceColumns(float a, float b, float r, float n) {
a = -a;
float m = min(a, b);
if ((a < r) && (b < r)) {
vec2 p = vec2(a, b);
float columnradius = r*sqrt(2)/n/2.0;
columnradius = r*sqrt(2)/((n-1)*2+sqrt(2));

rot45(p);
p.y += columnradius;
p.x -= sqrt(2)/2*r;
p.x += -columnradius*sqrt(2)/2;

if (mod(n,2) == 1) {
p.y += columnradius;
}
pMod1(p.y,columnradius*2);

float result = -length(p) + columnradius;
result = max(result, p.x);
result = min(result, a);
return -min(result, b);
} else {
return -m;
}
}

        float fOpUnionStairs(float a, float b, float r, float n) {
float s = r/n;
float u = b-r;
return min(min(a,b), 0.5 * (u + a + abs ((mod (u - a + s, 2 * s)) - s)));
}

vec2 opRepCircular(vec2 p, float period)
{
return vec3(atan(p.y,p.x)*period/TAU,length(p));
}

float pModPolar(vec2 p, float repetitions) {
float angle = 2*PI/repetitions;
float a = atan(p.y, p.x) + angle/2.;
float r = length(p);
a = mod(a,angle) - angle/2.;
p = vec2(cos(a), sin(a))*r;
return p;
}
